require 'spec_helper'
require 'ciri/pow_chain/header'
require 'ciri/utils'

RSpec.describe Ciri::POWChain::Header do

  it 'compute header hash' do
    raw_header_rlp = ''
    header_hash = Ciri::Utils.dehex 'b4fbadf8ea452b139718e2700dc1135cfc81145031c84b7ab27cd710394f7b38'
    # get binary version
    raw_header_rlp_b = Ciri::Utils.dehex raw_header_rlp
    header = Ciri::POWChain::Header.rlp_decode(raw_header_rlp_b)
    expect(header.get_hash).to eq header_hash
  end
end
